package com.danycarreto.lifecycleimport android.support.v7.app.AppCompatActivityimport android.os.Bundleimport android.util.Logimport android.widget.TextViewconst val GAME_STATE_KEY: String = "GAME_STATE_KEY"const val TEXT_VIEW_KEY: String = "TEXT_STATE_KEY"class MainActivity : AppCompatActivity() {    lateinit var textView: TextView    var gameState: String? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        Log.d(MainActivity::class.java.name,"onCreate()")        gameState = savedInstanceState?.getString(GAME_STATE_KEY)        setContentView(R.layout.activity_main)        textView = findViewById(R.id.text_view)        textView.text = "IÂ´m @DanyCarreto"    }    override fun onStart() {        Log.d(MainActivity::class.java.name,"onStart()")        super.onStart()    }    override fun onResume() {        Log.d(MainActivity::class.java.name,"onResume()")        super.onResume()    }    override fun onPause() {        Log.d(MainActivity::class.java.name,"onPause()")        super.onPause()    }    override fun onStop() {        super.onStop()        Log.d(MainActivity::class.java.name,"onStop()")    }    override fun onDestroy() {        super.onDestroy()        Log.d(MainActivity::class.java.name,"onDestroy()")    }    override fun onSaveInstanceState(outState: Bundle?) {        outState?.run {            putString(GAME_STATE_KEY, gameState)            putString(TEXT_VIEW_KEY, textView.text.toString())        }        super.onSaveInstanceState(outState)    }    override fun onRestoreInstanceState(savedInstanceState: Bundle?) {        super.onRestoreInstanceState(savedInstanceState)        textView.text = savedInstanceState?.getString(TEXT_VIEW_KEY)    }}